saldo = 1_000_000

print("halo, selamat datang di bank BE EF")

while True:
    print("saldo anda " + str(saldo))

    if saldo <=0:
        try:
            jawaban = int(input ("ketik 2 untuk memasukkan uang, ketik 0 untuk membatalkan "))
        except ValueError:
            print("tolong masukkan angka")
            continue
        
        if jawaban == 2:
            while True:
                try:
                    deposit = int(input ("masukkan deposit "))
                    if deposit < 0:
                        print("tolong masukkan angka positive")
                        break
                    else:
                        saldo = saldo + deposit
                        break
                except ValueError:
                    print("masukkan angka yang sesuai")
                    continue
                
        elif jawaban == 0:
            print("terimakasih sudah menggunakan layanan bank kami")
            break
        else:
            print (" tolong masukkan angka yang benar")
        
    else:
        try:
            jawaban = int(input ("ketik 1 untuk menarik uang, ketik 2 untuk memasukkan uang, ketik 0 untuk membatalkan "))
        except ValueError:
            print("tolong masukkan angka")
            continue
        
        if jawaban == 1:
            while True:
                try:
                    menarik = int(input ("masukkan jumlah "))
                    if menarik < 0:
                        print("tolong masukkan angka positive")
                        break
                    else:
                        if menarik > saldo:
                            print ("mohon maaf saldo anda tidak mencukupi")
                            print ("saldo anda " + str(saldo))
                        else:    
                            saldo = saldo - menarik
                            break
                except ValueError:
                    print("masukkan angka yang sesuai")
                    continue
                
        elif jawaban == 2:
            while True:
                try:
                    deposit = int(input ("masukkan deposit "))
                    if deposit < 0:
                        print ("tolong masukkan angka positive")
                        break
                    else:
                        saldo = saldo + deposit
                        break
                except ValueError:
                    print("masukkan angka yang sesuai")
                    continue
            
        elif jawaban == 0:
            print("terimakasih sudah menggunakan layanan bank kami")
            break
        
        else:
            print (" tolong masukkan angka yang benar")
            
print ("saldo anda " + str(saldo))
print ("TOLONG BUKA FILE PY INI UNTUK MELIHAT LEBIH DETAIL")



